<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of gndivision * * @author sourcecode.lk */class Gndivision {    public $id;    public $district_id;    public $ds_division_id;    public $gn_division_id;    public $name;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `gndivision` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->district_id = $result['district_id'];            $this->ds_division_id = $result['ds_division_id'];            $this->gn_division_id = $result['gn_division_id'];            $this->name = $result['name'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `gndivision` (`district_id`,`ds_division_id`,`name`,`queue`) VALUES  ('"                . $this->district_id . "', '"                . $this->ds_division_id . "', '"                 . $this->name . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return mysqli_insert_id($db->DB_CON);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `gndivision` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `gndivision` SET "                . "`district_id` ='" . $this->district_id . "', "                . "`ds_division_id` ='" . $this->ds_division_id . "', "                . "`gn_division_id` ='" . $this->gn_division_id . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function GetGnByDsdivision($id) {        $query = "SELECT * FROM `gndivision` WHERE `ds_division_id` = '" . $id . "'  ";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function delete() {        $query = 'DELETE FROM `gndivision` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function arrange($key, $img) {        $query = "UPDATE `gndivision` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }    public function getActivitiesByTitle($gndivision) {        $query = "SELECT `id` FROM `gndivision` WHERE `gndivision` LIKE '" . $gndivision . "'";        $db = new Database();        $result = mysqli_fetch_array($db->readQuery($query));        return $result['id'];    }}